create table appeals (appeal_id varchar(36) not null, created_at timestamp not null, created_by varchar(255) not null, modified_at timestamp, modified_by varchar(255), version bigint, additional_data varchar(255), additional_notes TINYTEXT, admin_notes TEXT, appeal_reason TEXT not null, ban_justified boolean not null, ban_reason TINYTEXT not null, ban_type varchar(255) not null, discord_username varchar(32), twitch_username varchar(25) not null, judgement_id varchar(36), previous_appeal_id varchar(36), primary key (appeal_id));
create table banned_by (banned_by_id varchar(36) not null, ban_date varchar(20) not null, name TINYTEXT not null, appeal_id varchar(36) not null, primary key (banned_by_id));
create table evidence (evidence_id varchar(36) not null, created_at timestamp not null, created_by varchar(255) not null, modified_at timestamp, modified_by varchar(255), version bigint, file_extension varchar(6) not null, notes TINYTEXT, appeal_id varchar(36) not null, primary key (evidence_id));
create table judgement (judgement_id varchar(36) not null, created_at timestamp not null, created_by varchar(255) not null, modified_at timestamp, modified_by varchar(255), version bigint, notes TINYTEXT, resubmit_dt timestamp, status varchar(21) not null, primary key (judgement_id));
